function plant_icon($i)
{
    $icon_slug = get_theme_mod('icon_' . $i);
    if ($icon_slug) {
        if ($icon_slug == 'custom') {
            return  get_theme_mod('icon_' . $i . '_custom');
        } else {
            return plant_icon_svg($icon_slug);
        }
    } else {
        return plant_icon_svg($i);
    }
}

if (!function_exists('seed_icon')) :
    function seed_icon($i = null)
    {
        if (!$i) {
            return;
        }
        $file = get_theme_file_path('/assets/img/i/' . $i . '.svg');
        if (file_exists($file)) {
            include get_theme_file_path('/assets/img/i/' . $i . '.svg');
        }
    }
endif;
